     * Closes snackbar
     */
    private resetSnackbar() {
        this.snackbar.dismiss();
    }

    /**
     * Handles successful login
     * @param response Login response
     */
    private handleLogin(response) {
        this.resetSnackbar();
        this.snackbar.open("Авторизация успешна", "Ok", { duration: 5000 });
        this.router.navigate([""]);
    }

