        event.preventDefault();
        if (this.login.valid) {
            this.account.logIn(value).subscribe((val) => {
                this.handleLogin(val);
            }, (err) => {
                this.handleError(err);
            });
        }
    }

    /**
     * Resets login form
     * @param event Reset event
     */
    public resetForm(event: Event) {
        this.login.reset();
        event.preventDefault();
    }
}

