import { Component, OnInit, Output } from "@angular/core";
import { AccountService } from "../services/account.service";
import { FormGroup, FormControl, Validators, FormBuilder } from "@angular/forms";
import { Utils } from "../../main/utils/utils";
import { Response } from "@angular/http";
import { Router } from "@angular/router";
import { MdSnackBar } from "@angular/material";
import { IErrorMessages } from "../../main/inputs/reactive-input.component";

export let infoErrorMessages = {
    required: "Поле обязательно для заполнения",
    maxlength: "Не более 40 сиволов"
};

export let accErrorMessages = {
    email: "Неправильный email",
    minlength: "Пароль должен быть не менее 6 символов",
    required: "Поле обязательно для заполнения",
    sameFields: "Пароли должны совпадать"
};

@Component({
    selector: "create-user",
    template: `
    <form class="login-form" [formGroup]="create" (ngSubmit)="submitForm($event, create.value)">
        <md-list class="content-window">
            <md-list-item class="form-item"><h1>Создать аккаунт</h1></md-list-item>
            <md-list-item class="form-input form-item">
                <reactive-input [group]="create" [errorList]="infoErrorMessages" [controlName]="'name'" [type]="'text'" [placeholder]="'Имя'" required="true">
                </reactive-input>
            </md-list-item>
            <md-list-item class="form-input form-item">
                <reactive-input [group]="create" [errorList]="infoErrorMessages" [controlName]="'surname'" [type]="'text'" [placeholder]="'Фамилия'" required="true">
                </reactive-input>
            </md-list-item>
            <md-list-item class="form-input form-item">
                <reactive-input [group]="create" [errorList]="infoErrorMessages" [controlName]="'lastName'" [type]="'text'" [placeholder]="'Отчество'" required="true">
                </reactive-input>
            </md-list-item>
            <md-list-item class="form-input form-item">
                <reactive-input [group]="create" [errorList]="accErrorMessages" [controlName]="'email'" [type]="'email'" [placeholder]="'Email'" required="true">
                </reactive-input>
            </md-list-item>
            <md-list-item class="form-input form-item">
                <reactive-input [group]="create" [errorList]="accErrorMessages" [controlName]="'password'" [type]="'password'" [placeholder]="'Пароль'" required="true">
                </reactive-input>
            </md-list-item>
            <md-list-item class="form-input form-item">
                <reactive-input [group]="create" [errorList]="accErrorMessages" [controlName]="'repeatPassword'" [type]="'password'" [placeholder]="'Повторите пароль'" required="true">
                </reactive-input>
            </md-list-item>
            <md-list-item class="form-item">
                <div class="link-wrapper">
                    <a routerLink="../login" routerLinkActive="active">Уже есть аккаунт?</a>
                </div>
                <div class="button-wrapper">
                    <button md-raised-button class="clean-login account" type="reset" (click)="resetForm($event)">Очистить</button>
                    <button md-raised-button class="send-login" type="submit">Отправить</button>
                </div>
            </md-list-item>
        </md-list>
    </form>`
})
export class CreateUserComponent implements OnInit {
    // services
    private fb: FormBuilder;
    private account: AccountService;
    private snackbar: MdSnackBar;
    private router: Router;
    private utils: Utils;
    // inputs
    @Output() public create: FormGroup;
    @Output() public infoErrorMessages: IErrorMessages;
    @Output() public accErrorMessages: IErrorMessages;
    constructor(fb: FormBuilder, account: AccountService, utils: Utils, snackbar: MdSnackBar, router: Router) {
        this.fb = fb;
        this.account = account;
        this.snackbar = snackbar;
        this.router = router;
        this.utils = utils;
        this.infoErrorMessages = infoErrorMessages;
        this.accErrorMessages = accErrorMessages;
    }
    public ngOnInit() {
        this.initForm();
    }

    /**
     * Initializes form
     */
    public initForm() {
        let passwordInput = this.fb.control("", [ Validators.required, Validators.minLength(6) ]);
        this.create = this.fb.group({
            name: this.fb.control("", [ Validators.required, Validators.maxLength(40) ]),
            surname: this.fb.control("", [ Validators.required, Validators.maxLength(40) ]),
            lastName: this.fb.control("", [ Validators.required, Validators.maxLength(40) ]),
            email: this.fb.control("", [ Validators.required, Validators.email ]),
            password: passwordInput,
            repeatPassword: this.fb.control("", [ Validators.required, Validators.minLength(6), this.utils.Validators.sameFields(passwordInput) ])
        });
    }

    /**
     * Closes snackbar
     */
    private resetSnackbar() {
        this.snackbar.dismiss();
    }

    /**
     * Handles user creation
     * @param response Successful http response
     */
    private handleCreate(response: Response) {
        this.resetSnackbar();
        this.snackbar.open("Аккаунт создан", "Ok", { duration: 5000 });
        this.router.navigate(["user", "login"]);
    }

    /**
     * Handles user creation error
     * @param error Http error
     */
    private handleErr(error: Response) {
        let parsedError = error.json();
        this.resetSnackbar();
        this.snackbar.open(`Ошибка: ${this.utils.translateErrorResponse(parsedError)}`, "Ok", { duration: 5000 });
        this.create.reset();
    }

    /**
     * Called on form submit
     * @param event Submit event
     * @param value Submit value
     */
    public submitForm(event: Event, value: any) {
        event.preventDefault();
        if (this.create.valid) {
            this.account.create({ email: value.email, password: value.password, name: value.name, surname: value.surname, lastName: value.lastName }).subscribe((res) => {
                this.handleCreate(res);
            }, (err) => {
                this.handleErr(err);
            });
        }
    }

    /**
     * Called on form reset
     * @param event Reset event
     */
    public resetForm(event: Event) {
        this.create.reset();
        event.preventDefault();
    }
}

import { Injectable, Inject } from "@angular/core";
import { ReplaySubject, Observable } from "rxjs";
import { Http, Response } from "@angular/http";
import { DatabaseClient, IUserDoc } from "../../database/database.module";
import { AccountHttpService } from "../../http/http.module";
import { LoadingService } from "../../main/load/loading.service";
import { AccountDatabaseService } from "../../database/account-database.service";
import { InMemoryStore } from "../../database/inMemoryStore";

/**
 * Data for creating user
 */
export interface ICreate {
    email: string;
    password: string;
    name: string;
    surname: string;
    lastName: string;
}

/**
 * Login credentials
 */
export interface ILoginCredentials {
    email: string;
    password: string;
    remember: boolean;
}

/**
 * Response from logging in
 */
export interface ILoginResponse {
    token: string;
    email: string;
    roles: string[];
    name: string;
    surname: string;
    lastName: string;
}

/**
 * States of account
 */
export enum TokenState {
    "pending",
    "unauthorized",
    "authorized"
}

@Injectable()
export class AccountService {
    private pouch: PouchDB.Static;
    private http: AccountHttpService;
    private database: AccountDatabaseService;
    private loading: LoadingService;
    public state: ReplaySubject<TokenState>;
    constructor(@Inject(AccountDatabaseService) database: AccountDatabaseService, @Inject(AccountHttpService) http: AccountHttpService, @Inject(Window) window: Window, @Inject(LoadingService) loading: LoadingService) {
        this.pouch = (window as any).PouchDB;
        this.state = new ReplaySubject();
        this.http = http;
        this.loading = loading;
        this.database = database;
    }

    /**
     * Initializes account service
     */
    public initAccount() {
        if (!this.database.getDatabase().isSetDb()) {
            this.state.next(TokenState["pending"]);
            this.database.addDatabase(new this.pouch("user", { auto_compaction: true }));
            this.checkAuth().subscribe((response) => {
                this.state.next(TokenState["authorized"]);
            }, (err) => {
                this.state.next(TokenState["unauthorized"]);
            });
        }
    }

    /**
     * Sets database store to use either PouchDB or InMemoryStore
     * @param persistent Will database data be persistent
     */
    private setProperStore(persistent: boolean) {
        if (persistent) {
            this.database.setProperDb("Te", this.pouch, "user", { auto_compaction: true });
        } else {
            this.database.setProperDb("InMemoryStore", InMemoryStore);
        }
    }

    /**
     * Checks if user is logged in
     */
    public checkAuth(): Observable<IUserDoc> {
        this.loading.onStart();
        return this.database.getDatabase().getRecord(Observable.of(["token", {}])).do(() => {
            this.loading.onEnd();
        }, () => {
            this.loading.onEnd();
        });
    }

    /**
     * Handles login response
     * @param response Observable with response
     * @param remember Should token be remembered
     */
    private handleLogin(response: Observable<Response>, remember: boolean) {
        return response.map((response) => {
            this.loading.onComplete(50);
            this.setProperStore(remember);
            let parsedResponse = response.json();
            return [ Object.assign(parsedResponse, { "_id": "token" }), {} ];
        });
    }

    /**
     * Logs in user
     * @param credentials Login credentials and should auth be remembered
     */
    public logIn(credentials: ILoginCredentials): Observable<any> {
        this.loading.onStart();
        let httpResponse = this.http.loginUser(Observable.of(credentials));
        let loginHandled = this.handleLogin(httpResponse, credentials.remember);
        let databaseResponse = this.database.getDatabase().putRecord(loginHandled);
        return databaseResponse.do((value) => {
            this.loading.onEnd();
            this.state.next(TokenState["authorized"]);
        }, (error) => {
            this.loading.onEnd();
            this.state.next(TokenState["unauthorized"]);
        });
    }

    /**
     * Logs off user
     */
    public logOff() {
        this.loading.onStart();
        let database = this.database.getDatabase();
        let getToken = database.getRecord(Observable.of(["token", {}])).map((response) => {
            return [ response, {} ];
        });
        let deleteToken = database.removeRecord(getToken);
        return deleteToken.do(() => {
            this.state.next(TokenState["unauthorized"]);
            this.loading.onEnd();
        }, () => {
            this.loading.onEnd();
        });
    }

    /**
     * Creates user
     */
    public create(credentials: ICreate) {
        this.loading.onStart();
        let httpResponse = this.http.createUser(Observable.of(credentials));
        return httpResponse.do(() => {
            this.loading.onEnd();
        }, () => {
            this.loading.onEnd();
        });
    }
}

