import { Injectable, Inject } from "@angular/core";
import { Observable } from "rxjs";
import { BaseDatabaseClient } from "./databaseClient";
import { Utils } from "../main/utils/utils";
import { IAccountDatabase, IDatabase } from "./interfaces";

@Injectable()
export class AccountDatabaseService {
    private database: BaseDatabaseClient;
    constructor(@Inject(Utils) utils: Utils) {
        this.database = new BaseDatabaseClient(utils);
    }

    /**
     * Creates database in pouchdb
     * @param name Name for database
     * @param factory Factory for database creation
     */
    public addDatabase(db: IAccountDatabase | IDatabase) {
        this.database.setDb(db);
    }

    /**
     * Removes database by name
     */
    public removeDatabase() {
        let database = this.getDatabase();
        if (database) {
            return database.dispose();
        } else {
            throw new Error("No database found");
        }
    }

    /**
     * Checks if needed database is set and if not sets it
     * @param name Database constructor name
     * @param database Database constructor
     * @param dbName Database name
     * @param options Database options
     */
    public setProperDb(name: string, database: any, dbName?: string, options?: any) {
        if (!this.database.checkDb(name)) {
            this.database.dispose();
            this.database.setDb(new database(dbName, options));
        }
    }

    /**
     * Gets database from a name
     * @param name Database name
     */
    public getDatabase() {
        return this.database;
    }
}

import { IDatabaseError, IDatabaseRecord, databaseOptions, IAccountDatabase, IUserDoc, IDatabase } from "./interfaces";
import { Utils } from "../main/utils/utils";
import { Observable } from "rxjs";

export class BaseDatabaseClient {
    public database: IAccountDatabase;
    public handler: Utils;
    constructor(handler: Utils) {
        this.handler = handler;
    }

    /**
     * Gets record from database
     * @param request Observable with database get request
     */
    public getRecord(request: Observable<[string, databaseOptions]>) {
        return request.map((req) => {
            return this.handler.handleResponse(this.database.get(req[0], req[1]) as any);
        }).concatAll();
    }

    /**
     * Sets record in database
     * @param request Observable with item to set
     */
    public putRecord(request: Observable<[any, databaseOptions]>) {
        return request.map((req) => {
            return this.handler.handleResponse(this.database.put(req[0], req[1]));
        }).concatAll();
    }

    /**
     * Removes record from database
     * @param request Observable with item to set
     */
    public removeRecord(request: Observable<[any, databaseOptions]>) {
        return request.map((req) => {
            return this.handler.handleResponse(this.database.remove(req[0], req[1]));
        }).concatAll();
    }

    /**
     * Sets database
     * @param database Database to use
     */
    public setDb(database: IAccountDatabase | IDatabase) {
        this.database = database;
    }

    /**
     * Checks if database belongs to certain type
     * @param maker Name of the database constructor
     */
    public checkDb(maker: string) {
        return this.database.constructor.name === maker;
    }

    /**
     * Returns if database is set
     */
    public isSetDb() {
        return !!this.database;
    }

    /**
     * Disposes database connection
     */
    public dispose() {
        return this.database.close();
    }
}

/**
 * Client for working with database
 */
export class DatabaseClient<T> extends BaseDatabaseClient {
    public database: IDatabase;
    constructor(handler: Utils) {
        super(handler);
    }

    /**
     * Gets all records from database
     * @param request Observable with requst options
     */
    public getAllRecords(request: Observable<databaseOptions>) {
        return request.map((req) => {
            return this.handler.handleResponse(this.database.allDocs(req));
        }).concatAll();
    }

    /**
     * Sets all records in database
     * @param request Observable with item array and optons
     */
    public putAllRecords(request: Observable<[any[], databaseOptions]>) {
        return request.map((req) => {
            return this.handler.handleResponse(this.database.bulkDocs(req[0], req[1]));
        }).concatAll();
    }
}

import { IAccountDatabase } from "./interfaces";

/**
 * Stores information in memory
 */
export class InMemoryStore<T> implements IAccountDatabase {
    private store: { [item: string]: T };
    constructor(store?: { [item: string]: T }) {
        if (!store) {
            this.store = {};
        } else {
            this.store = store;
        }
        // console.log(this.store);
    }

    /**
     * Returns document by id
     * @param id Id of document
     * @param options Options for getting document
     */
    public get(id: string, options?: any) {
        if (this.store[id]) {
            return Promise.resolve(this.store[id]);
        } else {
            return Promise.reject({ error: true, message: "not found", name: "get", reason: "", status: 404});
        }
    }

    /**
     * Puts new document in database
     * @param item Item to put
     * @param options Options for putting options
     */
    public put(item: T, options?: any) {
        this.store[(item as any)._id] = item;
        return Promise.resolve({ "OK": true });
    }

    /**
     * Removes document
     * @param doc Database document
     * @param options Database removal options
     */
    public remove(doc: T, options?: any) {
        if (this.store[(doc as any)._id]) {
            delete this.store[(doc as any)._id];
            return Promise.resolve({ "OK": true });
        } else {
            return Promise.reject({ error: true, message: "not found", name: "get", reason: "", status: 404});
        }
    }

    /**
     * Closes database
     */
    public close() {
        this.store = null;
        return Promise.resolve(true);
    }
}

